(function () {
    load("classpath:moment.js");
    load("classpath:moment-timezone.js");
    var returnValue = false;
    var comparison = false;
    var comparator = "is";
    var operatorValue = "before";
    var time = "07:00";
    var tenantTimezone = "";

    if (tenant && tenant.configurations && tenant.configurations.timeZone) {
        tenantTimezone = tenant.configurations.timeZone;
    }
    var tenantTime = moment.tz(tenantTimezone).format('HH:mm');
    
    if (operatorValue === 'before') {
        comparison = tenantTime <= time;
    } else if (operatorValue === 'after') {
        comparison = tenantTime >= time;
    }
        
    if (comparator === "is") {
        returnValue = comparison;
    } else {
        returnValue = !comparison;
    }
    return returnValue;
})();
